  )
}
class board extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      username: null,
      books : Array
    };
  }

  componentDidMount() {
    fetch('api')
      .then((res) => res.json())
      .then((data) => this.setState({ username: data.username }));
    fetch('api/text')
      .then((res) => res.json())
      .then((data) => this.setState({ textname: data.textname }));
    fetch('api/writer')
      .then((res) => res.json())
      .then((data) => this.setState({ writername: data.writername }));
    fetch('api/datetime')
      .then((res) => res.json())
      .then((data) => this.setState({ datetime: data.datetime }));
    axios.get('http://localhost:3001/books').then(res=>{
      let data = res.data.books;
      this.setState({books : data});
      data.forEach((key,value)=>[
          console.log(key)
      ])
    })
  }
  boardTable(){
    return(
      <div>
        {this.state.books.forEach((key)=>{
          <th>
            <td> {key.username}</td>
            <td>
              {key.textname}
            </td>
            <td> {key.writername}</td>
            <td> {key.datetime}</td>
            <td class="d-print-none">
                        <a
                          class="btn btn-sm btn-warning"
                          href="/edit/<%= book.Book_ID %>"
                        >
                          Edit
                        </a>
                        <a
                          class="btn btn-sm btn-danger"
                          href="/delete/<%= book.Book_ID %>"
                        >
                          Delete
                        </a>
                        <a class="btn btn-sm" href="/more/<%= book.Book_ID %>">
                          More
                        </a>
                      </td>
          </th>
        })}
      </div>
    )    
  }
  render() {
    const { username } = this.state;
    const { textname } = this.state;
    const { writername } = this.state;
    const { datetime } = this.state;

    return (
      <>
        <Top />
        <div className={styles.title}> 게시판 </div>
        <div class="container">
          <link
            rel="stylesheet"
            href="https://maxcdn.bootstrapcdn.com/bootstrap/4.0.0/css/bootstrap.min.css"
            integrity="sha384-Gn5384xqQ1aoWXA+058RXPxPg6fy4IWvTNh0E263XmFcJlSAwiGgFAW/dAiS6JXm"
            crossorigin="anonymous"
          />

          <div className="App">
            <ul>
              <div class="table-responsive-sm">
                <table class="table table-hover">
                  <thead>
                    <tr>
                      <th>번호</th>
                      <th>제목</th>
                      <th>작성자</th>
                      <th>작성일</th>
                      <th class="d-print-none">
                        <a class="btn btn-sm btn-success" href="/create">
                          Add
                        </a>
                      </th>
                    </tr>
                  </thead>

                  <tbody>

                    <tr>
                      <td class="d-print-none">
                        <a
                          class="btn btn-sm btn-warning"
                          href="/edit/<%= book.Book_ID %>"
                        >
                          Edit
                        </a>
                        <a
                          class="btn btn-sm btn-danger"
                          href="/delete/<%= book.Book_ID %>"
                        >
                          Delete
                        </a>
                        <a class="btn btn-sm" href="/more/<%= book.Book_ID %>">
                          More
                        </a>
                      </td>
                    </tr>
                  </tbody>

                  <tbody></tbody>
                </table>
              </div>
            </ul>
          </div>
        </div>
        <Bottom />{' '}
      </>
    );
  }
}

export default Board;
